//ternary operator
function printDay(day){
    switch (day) {
      case 1:
        console.log('monday')
        // code
        break;
      case 2:
        console.log('tuesday')
        break;
      case 3:
        console.log('wednesday')
        break
      case 4:
        console.log('thrusday')
        break;
      case 5:
        console.log('friday')
        break
      case 6:
        console.log('saturday')
        break
      case 7:
        console.log('sunday')
        break
      
      default:
      console.log('written by DRJ SINGH')
    }
  }
  let day=4 
  printDay(day)